def analyze_numbers(numbers):
    """Analyze list of numbers"""
    stats = {
        'min': min(numbers),
        'max': max(numbers),
        'average': sum(numbers) / len(numbers)
    }
    return stats

# Test
nums = [10, 20, 30, 40, 50]
result = analyze_numbers(nums)
print(result)
# {'min': 10, 'max': 50, 'average': 30.0}

