def calculate_grades(*scores, passing_grade=60):
    """
    Calculate grades for multiple scores.

    Returns:
        dict: Statistics and grade list
    """
    if not scores:
        return {"error": "No scores provided"}

    grades = []
    for score in scores:
        if score >= 90:
            grade = 'A'
        elif score >= 80:
            grade = 'B'
        elif score >= 70:
            grade = 'C'
        elif score >= passing_grade:
            grade = 'D'
        else:
            grade = 'F'
        grades.append(grade)

    average = sum(scores) / len(scores)
    passing = sum(1 for s in scores if s >= passing_grade)

    return {
        "scores": scores,
        "grades": grades,
        "average": average,
        "passing_count": passing,
        "total_count": len(scores)
    }

# Usage
result = calculate_grades(85, 92, 78, 45, 88)
print(f"Average: {result['average']:.2f}")
print(f"Grades: {result['grades']}")
print(f"Passing: {result['passing_count']}/{result['total_count']}")
