practice_problems.solution:
num = int(input("Enter number: "))

if num % 2 == 0:
    print(f"{num} is even")
else:
    print(f"{num} is odd")

age = int(input("Enter age: "))
is_citizen = input("Are you a citizen? (yes/no): ").lower() == "yes"

can_vote = (age >= 18) and is_citizen

if can_vote:
    print("You can vote!")
else:
    print("You cannot vote.")
    if age < 18:
        print("Reason: Under 18")
    if not is_citizen:
        print("Reason: Not a citizen")

real_world_use_cases.code:
score = int(input("Enter score (0-100): "))

# Chained comparison
if 90 <= score <= 100:
    grade = 'A'
elif 80 <= score < 90:
    grade = 'B'
elif 70 <= score < 80:
    grade = 'C'
elif 60 <= score < 70:
    grade = 'D'
else:
    grade = 'F'

print(f"Grade: {grade}")

# Pass/Fail
passed = score >= 60
print(f"Status: {'Pass' if passed else 'Fail'}")
