syntax:
# if statement
if condition:
    # code block (4 spaces indentation)

# if-else statement
if condition:
    # code if True
else:
    # code if False

# if-elif-else statement
if condition1:
    # code for condition1
elif condition2:
    # code for condition2
elif condition3:
    # code for condition3
else:
    # code if all False

# Nested conditions
if outer_condition:
    if inner_condition:
        # nested code
    else:
        # nested else
else:
    # outer else

# Ternary operator (one-liner)
variable = value_if_true if condition else value_if_false

# Multiple conditions
if condition1 and condition2:
    # both must be True

if condition1 or condition2:
    # at least one must be True

examples.code:
# Example 4: Nested Conditions
age = 20
has_license = True
has_car = False

if age >= 18:
    print("Age check: Passed")
    if has_license:
        print("License check: Passed")
        if has_car:
            print("You can drive your car")
        else:
            print("You can drive a rented car")
    else:
        print("Get a license first")
else:
    print("Too young to drive")

# Example 6: Chained Comparison
score = 75

if 0 <= score <= 100:
    print("Valid score")
    if 90 <= score <= 100:
        print("Grade: A")
    elif 80 <= score < 90:
        print("Grade: B")
    elif 70 <= score < 80:
        print("Grade: C")
    else:
        print("Grade: Below C")
else:
    print("Invalid score")

common_mistakes.example:
# Multiple conditions needed? Use separate ifs:
if score >= 90:
    print("Excellent")
if score >= 80:  # This also checks, independent
    print("Good")

# vs if-elif (only first matches):
if score >= 90:
    print("Excellent")  # If this is True
elif score >= 80:
    print("Good")  # This is skipped!

practice_problems.solution:
num = int(input("Enter a number: "))

if num > 0:
    print("Positive")
elif num < 0:
    print("Negative")
else:
    print("Zero")

age = int(input("Enter age: "))
is_citizen = input("Are you a citizen? (yes/no): ").lower() == "yes"

if age >= 18 and is_citizen:
    print("You can vote!")
else:
    print("You cannot vote")
    if age < 18:
        print("Reason: Under 18")
    if not is_citizen:
        print("Reason: Not a citizen")

weight = float(input("Weight (kg): "))
height = float(input("Height (m): "))

bmi = weight / (height ** 2)

print(f"BMI: {bmi:.2f}")

if bmi < 18.5:
    print("Category: Underweight")
elif bmi < 25:
    print("Category: Normal")
elif bmi < 30:
    print("Category: Overweight")
else:
    print("Category: Obese")

CORRECT_USER = "admin"
CORRECT_PASS = "secret123"
CORRECT_CODE = "9999"

username = input("Username: ")

if username == CORRECT_USER:
    password = input("Password: ")
    if password == CORRECT_PASS:
        code = input("2FA Code: ")
        if code == CORRECT_CODE:
            print("Login successful!")
            print("Welcome, admin!")
        else:
            print("Invalid 2FA code")
    else:
        print("Incorrect password")
else:
    print("User not found")

real_world_use_cases.code:
cart_total = float(input("Cart total: $"))
is_premium = input("Premium member? (yes/no): ").lower() == "yes"

if cart_total >= 100:
    if is_premium:
        discount_percent = 20
    else:
        discount_percent = 10
elif cart_total >= 50:
    discount_percent = 5
else:
    discount_percent = 0

discount = cart_total * (discount_percent / 100)
final_total = cart_total - discount

print(f"\nCart Total: ${cart_total:.2f}")
print(f"Discount: {discount_percent}% (${discount:.2f})")
print(f"Final Total: ${final_total:.2f}")

temperature = int(input("Temperature (°C): "))
is_raining = input("Is it raining? (yes/no): ").lower() == "yes"

if is_raining:
    print("Recommendation: Stay indoors")
    print("- Watch movies")
    print("- Read a book")
else:
    if temperature > 30:
        print("It's hot!")
        print("Recommendation: Go swimming or stay in AC")
    elif temperature >= 20:
        print("Pleasant weather!")
        print("Recommendation: Perfect for outdoor activities")
    elif temperature >= 10:
        print("Cool weather")
        print("Recommendation: Wear a jacket, go for a walk")
    else:
        print("It's cold!")
        print("Recommendation: Stay warm, hot chocolate time")

exam_score = float(input("Exam score (0-100): "))
attendance = float(input("Attendance percentage: "))

# Attendance bonus
if attendance >= 90:
    bonus = 5
    print("Excellent attendance! +5 bonus points")
elif attendance >= 75:
    bonus = 2
    print("Good attendance! +2 bonus points")
else:
    bonus = 0
    print("Low attendance. No bonus")

final_score = min(exam_score + bonus, 100)  # Cap at 100

print(f"\nFinal Score: {final_score}")

# Determine grade
if final_score >= 90:
    print("Grade: A (Excellent)")
elif final_score >= 80:
    print("Grade: B (Very Good)")
elif final_score >= 70:
    print("Grade: C (Good)")
elif final_score >= 60:
    print("Grade: D (Pass)")
else:
    print("Grade: F (Fail)")

light_color = input("Traffic light color (red/yellow/green): ").lower()
speed = int(input("Current speed (km/h): "))
distance_to_light = int(input("Distance to light (meters): "))

if light_color == "red":
    print("ACTION: STOP immediately")
    if speed > 0:
        print("WARNING: You must come to a complete stop")
elif light_color == "yellow":
    print("ACTION: Prepare to stop")
    if distance_to_light > 30:
        print("You have time to stop safely")
    else:
        print("If safe, you may proceed with caution")
elif light_color == "green":
    print("ACTION: Proceed")
    if speed < 40:
        print("You may accelerate")
    else:
        print("Maintain safe speed")
else:
    print("ERROR: Invalid light color")
