real_world_use_cases.code:
# Admin permissions
admin_permissions = {"read", "write", "delete", "manage_users"}

# Editor permissions
editor_permissions = {"read", "write", "publish"}

# User's current permissions
user_permissions = {"read", "write"}

def check_access(user_perms, required_perms):
    """Check if user has all required permissions"""
    return required_perms.issubset(user_perms)

# Check if user can publish
required = {"read", "write", "publish"}
if check_access(user_permissions, required):
    print("User can publish")
else:
    missing = required - user_permissions
    print(f"Missing permissions: {missing}")

# Grant additional permissions
user_permissions.update({"publish"})
print(f"\nUpdated permissions: {user_permissions}")
