syntax:
if (condition) {
  // code
} else {
  // code
}

switch(expression) {
  case x:
    // code
    break;
  default:
    // code
}

examples.code:
let time = 20;
if (time < 12) {
  console.log("Good Morning");
} else if (time < 18) {
  console.log("Good Afternoon");
} else {
  console.log("Good Evening");
}
