def parse_url(url):
    """Parse URL into components"""
    # Remove protocol
    if '://' in url:
        protocol, rest = url.split('://', 1)
    else:
        protocol = None
        rest = url

    # Separate domain from path
    if '/' in rest:
        domain, path = rest.split('/', 1)
        path = '/' + path
    else:
        domain = rest
        path = '/'

    # Extract query parameters
    if '?' in path:
        path, query = path.split('?', 1)
    else:
        query = None

    return {
        "protocol": protocol,
        "domain": domain,
        "path": path,
        "query": query
    }

urls = [
    "https://example.com/page?id=123",
    "http://blog.example.com/posts/2024",
    "example.com/about"
]

for url in urls:
    print(f"
URL: {url}")
    parsed = parse_url(url)
    for key, value in parsed.items():
        print(f"  {key}: {value}")
