const [data, setData] = useState(null);
const [loading, setLoading] = useState(true);

useEffect(() => {
  fetch('https://api.example.com/items')
    .then(res => res.json())
    .then(data => {
       setData(data);
       setLoading(false);
    });
}, []);

if (loading) return <Spinner />;
