tutorial.syntax:
# for loop
for variable in sequence:
    # code block

# for with range
for i in range(5):        # 0 to 4
    # code

for i in range(2, 8):     # 2 to 7
    # code

for i in range(0, 10, 2): # 0,2,4,6,8
    # code

# while loop
while condition:
    # code block
    # update condition

# break - exit loop
for i in range(10):
    if condition:
        break

# continue - skip iteration
for i in range(10):
    if condition:
        continue

# pass - do nothing
for i in range(10):
    if condition:
        pass

# Nested loop
for i in sequence1:
    for j in sequence2:
        # code

# Loop with else
for i in sequence:
    # code
else:
    # runs if loop completes without break

# enumerate (with index)
for index, value in enumerate(list):
    # code

common_mistakes.example:
# break - exits completely
for i in range(5):
    if i == 2:
        break  # Stops at 2
    print(i)  # Output: 0 1

# continue - skips one iteration
for i in range(5):
    if i == 2:
        continue  # Skips 2
    print(i)  # Output: 0 1 3 4

practice_problems.solution:
for i in range(1, 11):
    print(i)

# Or with better formatting:
for i in range(1, 11):
    print(i, end=" ")
print()  # Newline at end

# Method 1: range with step
for i in range(2, 21, 2):
    print(i, end=" ")
print()

# Method 2: if condition
for i in range(1, 21):
    if i % 2 == 0:
        print(i, end=" ")

numbers = [45, 12, 78, 23, 91, 34]

max_num = numbers[0]  # Start with first

for num in numbers:
    if num > max_num:
        max_num = num

print(f"Largest: {max_num}")  # 91

# Method 1: String multiplication
for i in range(1, 6):
    print('*' * i)

# Method 2: Nested loop
for i in range(1, 6):
    for j in range(i):
        print('*', end='')
    print()  # Newline

real_world_use_cases.code:
n = 20
print(f"Prime numbers up to {n}:")

for num in range(2, n + 1):
    is_prime = True

    # Check if num is divisible by any number from 2 to num-1
    for i in range(2, num):
        if num % i == 0:
            is_prime = False
            break  # Not prime, stop checking

    if is_prime:
        print(num, end=" ")

print()
# Output: 2 3 5 7 11 13 17 19
